/*global tinymce:true */

tinymce.PluginManager.add('preview', function(editor) {
	var settings = editor.settings, sandbox = false && !tinymce.Env.ie;

	editor.addCommand('mcePreview', function() {
        $('#previewModal').modal('show');
        var container = document.querySelector('#previewModal .preview-content');
        container.innerHTML = editor.getContent();
        Numbas.jme.variables.DOMcontentsubvars(container,viewModel.questionScope());
        $(container).data('jme-scope',viewModel.questionScope());
        MathJax.Hub.Queue(["Typeset",MathJax.Hub,container]);

        return;
		editor.windowManager.open({
			title: 'Preview',
			width: parseInt(editor.getParam("plugin_preview_width", "650"), 10),
			height: parseInt(editor.getParam("plugin_preview_height", "500"), 10),
			html: 
                '<div class="mce-preview-content" ></div>' +
                '<footer class="mce-preview-footer">Variables have been substituted into this content.</footer>'
                ,
			buttons: {
				text: 'Close',
				onclick: function() {
					this.parent().parent().close();
				}
			},
			onPostRender: function() {
				var previewHtml, headHtml = '';

				headHtml += '<base href="' + editor.documentBaseURI.getURI() + '">';

				tinymce.each(editor.contentCSS, function(url) {
					headHtml += '<link type="text/css" rel="stylesheet" href="' + editor.documentBaseURI.toAbsolute(url) + '">';
				});

				var bodyId = settings.body_id || 'tinymce';
				if (bodyId.indexOf('=') != -1) {
					bodyId = editor.getParam('body_id', '', 'hash');
					bodyId = bodyId[editor.id] || bodyId;
				}

				var bodyClass = settings.body_class || '';
				if (bodyClass.indexOf('=') != -1) {
					bodyClass = editor.getParam('body_class', '', 'hash');
					bodyClass = bodyClass[editor.id] || '';
				}

				var preventClicksOnLinksScript = (
					'<script>' +
						'document.addEventListener && document.addEventListener("click", function(e) {' +
							'for (var elm = e.target; elm; elm = elm.parentNode) {' +
								'if (elm.nodeName === "A") {' +
									'e.preventDefault();' +
								'}' +
							'}' +
						'}, false);' +
					'</script> '
				);

				var dirAttr = editor.settings.directionality ? ' dir="' + editor.settings.directionality + '"' : '';

                previewHtml = editor.getContent();

				if (!sandbox) {
					// IE 6-11 doesn't support data uris on iframes
					// so I guess they will have to be less secure since we can't sandbox on those
					// TODO: Use sandbox if future versions of IE supports iframes with data: uris.
					var doc = this.getEl('body').querySelector('.mce-preview-content');
					doc.innerHTML = previewHtml;
                    setTimeout(function() {
                        Numbas.jme.variables.DOMcontentsubvars(doc,viewModel.questionScope());
                        MathJax.Hub.Queue(["Typeset",MathJax.Hub,doc]);
                    },1000);
				} else {
					this.getEl('body').firstChild.srcdoc = previewHtml;
				}
			}
		});
	});

	editor.addButton('preview', {
		title: 'Preview',
		cmd: 'mcePreview'
	});

	editor.addMenuItem('preview', {
		text: 'Preview',
		cmd: 'mcePreview',
		context: 'view'
	});
});

